      clk             => clk,
      reset           => reset,
      rejection       => rejection,
      coeff_sc_addr   => coeff_sc_addr,
      coeff_sc1       => coeff_sc1,
      coeff_sc1_valid => coeff_sc1_valid,
      coeff_sc2       => coeff_sc2,
